tables.table_schema:
CREATE TABLE Notifications (
    notification_id INT PRIMARY KEY,
    notification_message TEXT,
    event_id INT,
    FOREIGN KEY (event_id) REFERENCES GameEvents(event_id)
);

CREATE TABLE event_participants (
    participant_id INT PRIMARY KEY,
    participant_name VARCHAR(255) NOT NULL,
    event_id INT REFERENCES game_events(event_id) ON DELETE CASCADE
);

CREATE TABLE notifications (
    notification_id INT PRIMARY KEY,
    notification_text TEXT NOT NULL,
    event_id INT REFERENCES game_events(event_id) ON DELETE CASCADE
);

CREATE TABLE user_preferences (
    user_id INT PRIMARY KEY,
    user_name VARCHAR(255) NOT NULL,
    receive_notifications BOOLEAN,
    preferred_notification_time TIME
);

CREATE TABLE game_events (
   event_id INT PRIMARY KEY,
   event_name VARCHAR(255) NOT NULL,
   event_date DATE NOT NULL,
   event_time TIME NOT NULL
);

CREATE TABLE event_participants (
   participant_id INT PRIMARY KEY,
   event_id INT REFERENCES game_events(event_id),
   participant_name VARCHAR(255) NOT NULL,
   participant_email VARCHAR(255) NOT NULL
);

CREATE TABLE community_members (
   member_id INT PRIMARY KEY,
   member_name VARCHAR(255) NOT NULL,
   member_email VARCHAR(255) NOT NULL
);

CREATE TABLE member_preferences (
   preference_id INT PRIMARY KEY,
   member_id INT REFERENCES community_members(member_id),
   notification_setting BOOLEAN DEFAULT TRUE
);
