tables.table_schema:
CREATE TABLE player_interactions (
    player_id INT not null,
    interaction_type INT not null,
    interaction_timestamp TIMESTAMP not null
);

CREATE TABLE player_messages (
    message_id SERIAL PRIMARY KEY,
    player_id INT not null,
    message_text TEXT not null,
    message_timestamp TIMESTAMP not null
);

CREATE TABLE player_warnings (
    warning_id SERIAL PRIMARY KEY,
    player_id INT not null,
    warning_reason TEXT not null,
    warning_timestamp TIMESTAMP not null
);

CREATE TABLE player_interactions (
    player_id INT,
    interaction_time TIMESTAMP,
    interaction_type VARCHAR(50),
    interaction_description TEXT
);

CREATE TABLE player_warnings (
    warning_id INT,
    player_id INT,
    warning_time TIMESTAMP,
    warning_reason TEXT
);

CREATE TABLE Player (
    player_id INT PRIMARY KEY,
    player_name VARCHAR(255) NOT NULL,
    player_email VARCHAR(255) UNIQUE
);

CREATE TABLE Game (
    game_id INT PRIMARY KEY,
    game_name VARCHAR(255) NOT NULL,
    game_category VARCHAR(255)
);

CREATE TABLE Interaction (
    interaction_id INT PRIMARY KEY,
    player_id INT,
    game_id INT,
    interaction_type VARCHAR(255)
);

CREATE TABLE Communication (
    comm_id INT PRIMARY KEY,
    sender_id INT,
    receiver_id INT,
    message_text TEXT
);

CREATE TABLE Moderation (
    mod_id INT PRIMARY KEY,
    mod_type VARCHAR(255),
    moderator_id INT,
    mod_description TEXT
);
