tables.table_schema:
CREATE TABLE game_configurations (
  id INT PRIMARY KEY,
  player_id INT,
  game_setting_1 VARCHAR(255),
  game_setting_2 VARCHAR(255)
);

CREATE TABLE player_experiences (
  player_id INT PRIMARY KEY,
  experience_points INT,
  level INT
);

CREATE TABLE game_settings (
    setting_id INT PRIMARY KEY,
    setting_name VARCHAR(255) NOT NULL,
    setting_value VARCHAR(255) NOT NULL
);

CREATE TABLE player_profiles (
    player_id INT PRIMARY KEY,
    player_name VARCHAR(255) NOT NULL,
    player_level INT NOT NULL,
    player_score INT NOT NULL
);

CREATE TABLE player_preferences (
    preference_id INT PRIMARY KEY,
    player_id INT REFERENCES player_profiles(player_id),
    favorite_game VARCHAR(255) NOT NULL
);

CREATE TABLE GameConfigurations (
    config_id INT PRIMARY KEY,
    config_name VARCHAR(255),
    config_value TEXT
);

CREATE TABLE PlayerProfiles (
    player_id INT PRIMARY KEY,
    player_name VARCHAR(255),
    player_email VARCHAR(255),
    player_level INT
);

CREATE TABLE GameBonuses (
    bonus_id INT PRIMARY KEY,
    bonus_name VARCHAR(255),
    bonus_multiplier FLOAT
);

CREATE TABLE PlayerAchievements (
    achievement_id INT PRIMARY KEY,
    player_id INT,
    achievement_name VARCHAR(255),
    achievement_date DATE
);

CREATE TABLE game_settings (
    setting_id INT PRIMARY KEY,
    setting_name VARCHAR(50),
    setting_value VARCHAR(100)
);

CREATE TABLE player_profiles (
    player_id INT PRIMARY KEY,
    player_name VARCHAR(50),
    player_level INT
);

CREATE TABLE game_levels (
    level_id INT PRIMARY KEY,
    level_name VARCHAR(50),
    level_difficulty INT
);

CREATE TABLE player_scores (
    score_id INT PRIMARY KEY,
    player_id INT,
    level_id INT,
    score_value INT
);

CREATE TABLE player_inventory (
    inventory_id INT PRIMARY KEY,
    player_id INT,
    item_name VARCHAR(50),
    item_quantity INT
);
