tables.table_schema:
CREATE TABLE matchmaking_data (
    id SERIAL PRIMARY KEY,
    player_id INT,
    player_rank INT,
    match_id INT
);

CREATE TABLE game_session_data (
    session_id SERIAL PRIMARY KEY,
    match_id INT,
    start_time TIMESTAMP,
    end_time TIMESTAMP
);

CREATE TABLE game_servers (
    server_id SERIAL PRIMARY KEY,
    server_name VARCHAR(50) NOT NULL,
    server_location VARCHAR(50) NOT NULL
);

CREATE TABLE players (
    player_id SERIAL PRIMARY KEY,
    player_name VARCHAR(50) NOT NULL,
    player_rank INT NOT NULL,
    player_region VARCHAR(50) NOT NULL
);

CREATE TABLE matches (
    match_id SERIAL PRIMARY KEY,
    match_type VARCHAR(50) NOT NULL,
    match_duration INT NOT NULL,
    match_map VARCHAR(50) NOT NULL
);

CREATE TABLE sessions (
    session_id SERIAL PRIMARY KEY,
    session_type VARCHAR(50) NOT NULL,
    session_start TIMESTAMP NOT NULL,
    session_end TIMESTAMP
);

CREATE TABLE players (
    player_id UUID PRIMARY KEY,
    player_name VARCHAR(50),
    player_rank INT,
    registration_date DATE
);

CREATE TABLE game_servers (
    server_id UUID PRIMARY KEY,
    server_name VARCHAR(50),
    region VARCHAR(30),
    status BOOLEAN
);

CREATE TABLE session_players (
    session_id UUID,
    player_id UUID,
    player_status VARCHAR(20)
);

CREATE TABLE server_logs (
    log_id UUID PRIMARY KEY,
    server_id UUID,
    log_time TIMESTAMP,
    log_message VARCHAR(255)
);
