tables.table_schema:
CREATE TABLE currency_transactions (
    transaction_id INT PRIMARY KEY,
    player_id INT,
    transaction_amount DECIMAL,
    transaction_type VARCHAR(50),
    transaction_date TIMESTAMP
);

CREATE TABLE economy_balance (
    player_id INT PRIMARY KEY,
    balance DECIMAL
);

CREATE TABLE user_balance (
	user_id INT PRIMARY KEY,
	balance INT NOT NULL
);

CREATE TABLE economy_logs (
	log_id SERIAL PRIMARY KEY,
	description TEXT NOT NULL,
	log_date TIMESTAMP NOT NULL
);

CREATE TABLE user_currency_balance (
    user_id UUID PRIMARY KEY,
    balance DECIMAL(10, 2) DEFAULT 0.00 NOT NULL
);

CREATE TABLE currency_exchange_rates (
    currency_code VARCHAR(3) PRIMARY KEY,
    exchange_rate DECIMAL(10, 8) NOT NULL
);

CREATE TABLE currency_loot (
    loot_id UUID PRIMARY KEY,
    loot_name VARCHAR(100) NOT NULL,
    loot_value DECIMAL(10, 2) NOT NULL
);
