tables.table_schema:
CREATE TABLE live_broadcast_data (
    broadcast_id INT PRIMARY KEY,
    viewer_count INT,
    chat_activity TEXT
);

CREATE TABLE LiveBroadcastViewers (
    viewer_id INT PRIMARY KEY,
    viewer_count INT,
    chat_activity TEXT
);

CREATE TABLE viewer_engagement_data (
    broadcast_id INT PRIMARY KEY,
    viewers_count INT,
    chat_activity TEXT
);

CREATE TABLE broadcast_details (
    broadcast_id INT PRIMARY KEY,
    broadcast_name TEXT,
    broadcast_date TIMESTAMP
);

CREATE TABLE viewer_profile (
    viewer_id INT PRIMARY KEY,
    viewer_name TEXT,
    viewer_location TEXT,
    viewer_age INT
);

CREATE TABLE Viewers (
    viewer_id INT PRIMARY KEY,
    viewer_name VARCHAR(255),
    email VARCHAR(255),
    join_time TIMESTAMP
);

CREATE TABLE broadcasts (
    broadcast_id INT PRIMARY KEY,
    broadcast_name VARCHAR(50),
    start_time TIMESTAMP,
    end_time TIMESTAMP
);

CREATE TABLE viewer_stats (
    viewer_id INT PRIMARY KEY,
    total_messages_sent INT,
    total_viewing_time INT,
    last_activity_timestamp TIMESTAMP
);
