tables.table_schema:
CREATE TABLE feedback (
    feedback_id SERIAL PRIMARY KEY,
    player_id INT,
    feedback_text TEXT,
    submission_date DATE
);

CREATE TABLE PlayerFeedback (
    player_id INT PRIMARY KEY,
    player_name VARCHAR(50),
    feedback_text TEXT
);

CREATE TABLE GameReviews (
    review_id INT PRIMARY KEY,
    game_id INT,
    review_text TEXT,
    rating INT
);

CREATE TABLE PlayerScores (
    player_id INT,
    game_id INT,
    high_score INT,
    PRIMARY KEY (player_id, game_id)
);

CREATE TABLE player_feedback (
  player_id INT,
  feedback_id INT,
  feedback_text TEXT,
  date_received DATE
);

CREATE TABLE player_reviews (
  review_id INT,
  player_id INT,
  review_text TEXT,
  rating INT
);

CREATE TABLE games (
  game_id INT,
  game_title VARCHAR(50),
  release_date DATE,
  developer VARCHAR(50)
);

CREATE TABLE developers (
  developer_id INT,
  developer_name VARCHAR(50),
  headquarters_location VARCHAR(100)
);

CREATE TABLE Player (
    player_id INT PRIMARY KEY,
    player_name TEXT,
    email TEXT
);

CREATE TABLE Game (
    game_id INT PRIMARY KEY,
    game_title TEXT,
    release_date DATE
);

CREATE TABLE Developer (
    developer_id INT PRIMARY KEY,
    developer_name TEXT,
    headquarters TEXT
);
