tables.table_schema:
CREATE TABLE game_assets (
  asset_id UUID PRIMARY KEY,
  asset_name VARCHAR(255) NOT NULL,
  asset_type VARCHAR(50) NOT NULL,
  asset_file BYTEA NOT NULL
);

CREATE TABLE game_assets (
  id UUID PRIMARY KEY,
  asset_name VARCHAR(255),
  asset_type VARCHAR(50),
  asset_url TEXT
);

CREATE TABLE asset_metadata (
  id UUID PRIMARY KEY,
  asset_id UUID REFERENCES game_assets(id),
  metadata_key VARCHAR(255),
  metadata_value TEXT
);

CREATE TABLE GameAssets (
    asset_id UUID PRIMARY KEY,
    asset_name VARCHAR(255) NOT NULL,
    asset_type VARCHAR(50) NOT NULL,
    asset_size INT NOT NULL
);

CREATE TABLE GameAssets (
    asset_id INT PRIMARY KEY,
    asset_name VARCHAR(255) NOT NULL,
    asset_type ENUM('image', 'video', 'sound') NOT NULL,
    asset_size INT NOT NULL
);

CREATE TABLE GameAttributes (
    attribute_id INT PRIMARY KEY,
    attribute_name VARCHAR(255) NOT NULL,
    attribute_description TEXT
);

CREATE TABLE GameDevelopers (
    developer_id INT PRIMARY KEY,
    developer_name VARCHAR(255) NOT NULL,
    developer_email VARCHAR(255) UNIQUE
);
