tables.table_schema:
CREATE TABLE sponsor_companies (
    company_id INT PRIMARY KEY,
    company_name VARCHAR(255) NOT NULL,
    contact_person VARCHAR(100) NOT NULL
);

CREATE TABLE obligations (
    obligation_id INT PRIMARY KEY,
    contract_id INT NOT NULL,
    obligation_description TEXT NOT NULL,
    due_date DATE NOT NULL
);

CREATE TABLE obligations (
    obligation_id UUID PRIMARY KEY,
    obligation_description TEXT NOT NULL,
    due_date DATE,
    status VARCHAR(20)
);

CREATE TABLE sponsorship_agreements (
    agreement_id UUID PRIMARY KEY,
    company_id UUID REFERENCES sponsor_companies(company_id),
    term_id UUID REFERENCES contract_terms(term_id),
    obligation_id UUID REFERENCES obligations(obligation_id)
);

CREATE TABLE sponsor_companies (
  sponsor_id INT PRIMARY KEY,
  company_name VARCHAR(255) NOT NULL,
  industry VARCHAR(100),
  contact_person VARCHAR(255)
);

CREATE TABLE contract_terms (
  term_id INT PRIMARY KEY,
  term_description TEXT,
  start_date DATE,
  end_date DATE
);

CREATE TABLE obligations (
  obligation_id INT PRIMARY KEY,
  obligation_description TEXT,
  deadline DATE,
  completed BOOLEAN
);
