tables.table_schema:
CREATE TABLE PlayerAccounts (
    player_id INT PRIMARY KEY,
    username VARCHAR(50) NOT NULL,
    email VARCHAR(100) UNIQUE,
    password_hash VARCHAR(256) NOT NULL
);

CREATE TABLE Authentication (
    auth_id INT PRIMARY KEY,
    player_id INT REFERENCES PlayerAccounts(player_id),
    token VARCHAR(100) NOT NULL,
    last_login TIMESTAMP
);

CREATE TABLE player_accounts (
 id INT PRIMARY KEY,
 username VARCHAR(50) NOT NULL,
 email VARCHAR(100) NOT NULL,
 password VARCHAR(100) NOT NULL
);

CREATE TABLE player_authentication (
 player_id INT REFERENCES player_accounts(id),
 access_token VARCHAR(100) NOT NULL,
 refresh_token VARCHAR(100) NOT NULL,
 last_login TIMESTAMP
);

CREATE TABLE player_stats (
 player_id INT REFERENCES player_accounts(id),
 game_wins INT DEFAULT 0,
 game_losses INT DEFAULT 0,
 total_games_played INT DEFAULT 0
);

CREATE TABLE player_accounts (
    player_id INT PRIMARY KEY,
    username VARCHAR(50) NOT NULL,
    email VARCHAR(100) UNIQUE,
    password_hash VARCHAR(100) NOT NULL
);

CREATE TABLE player_inventory (
    inventory_id INT PRIMARY KEY,
    player_id INT REFERENCES player_accounts(player_id),
    item_name VARCHAR(50) NOT NULL,
    quantity INT NOT NULL
);

CREATE TABLE players (
    player_id INT PRIMARY KEY,
    player_name VARCHAR(50),
    email VARCHAR(100),
    password VARCHAR(100)
);
