tables.table_schema:
CREATE TABLE feedback (
  id INT PRIMARY KEY,
  player_name VARCHAR(255),
  feedback_text TEXT,
  rating INT
);

CREATE TABLE reviews (
  id INT PRIMARY KEY,
  reviewer_name VARCHAR(255),
  review_text TEXT,
  review_date TIMESTAMP
);

CREATE TABLE player_feedback (
    feedback_id INT PRIMARY KEY,
    player_id INT,
    feedback_text TEXT,
    timestamp TIMESTAMP
);

CREATE TABLE game_development (
    game_id INT PRIMARY KEY,
    development_team_id INT,
    development_progress TEXT,
    release_date DATE
);

CREATE TABLE player_feedback (
  id INT PRIMARY KEY,
