tables.table_schema:
CREATE TABLE game_level_data (
    id INT PRIMARY KEY,
    level_number INT,
    level_data JSONB
);

CREATE TABLE level_data (
    level_id INT PRIMARY KEY,
    level_name VARCHAR(255),
    difficulty INT
);

CREATE TABLE tile_data (
    tile_id INT PRIMARY KEY,
    tile_type VARCHAR(50),
    is_obstacle BOOLEAN,
    level_id INT,
    FOREIGN KEY (level_id) REFERENCES level_data(level_id)
);

CREATE TABLE game_levels(
  level_id INT PRIMARY KEY,
  level_seed VARCHAR(255) NOT NULL,
  level_difficulty INT
);

CREATE TABLE GameLevels (
    level_id INT PRIMARY KEY,
    level_name VARCHAR(255),
    level_difficulty INT
);
