tables.table_schema:
CREATE TABLE attendance_records (
    id UUID PRIMARY KEY,
    member_id UUID,
    check_in_time TIMESTAMP,
    check_out_time TIMESTAMP
);

CREATE TABLE shift_schedules (
    id UUID PRIMARY KEY,
    member_id UUID,
    shift_start_time TIMESTAMP,
    shift_end_time TIMESTAMP
);

CREATE TABLE team_members (
    member_id INT PRIMARY KEY,
    member_name VARCHAR(255),
    position VARCHAR(255)
);

CREATE TABLE attendance (
    attendance_id INT PRIMARY KEY,
    shift_id INT REFERENCES shifts(shift_id),
    member_id INT REFERENCES team_members(member_id),
    attendance_status VARCHAR(255)
);

CREATE TABLE team_members (
    member_id INT PRIMARY KEY,
    member_name VARCHAR(50) NOT NULL,
    member_email VARCHAR(50) UNIQUE NOT NULL
);

CREATE TABLE shifts (
    shift_id INT PRIMARY KEY,
    shift_date DATE NOT NULL,
    start_time TIME NOT NULL,
    end_time TIME NOT NULL
);

CREATE TABLE staffing_coverage (
    coverage_id INT PRIMARY KEY,
    shift_id INT NOT NULL,
    required_staff_count INT NOT NULL,
    FOREIGN KEY (shift_id) REFERENCES shifts(shift_id)
);

CREATE TABLE schedules (
    schedule_id INT PRIMARY KEY,
    date DATE,
    shift_id INT,
    FOREIGN KEY (shift_id) REFERENCES shifts(shift_id)
);

CREATE TABLE teams (
    team_id INT PRIMARY KEY,
    team_name VARCHAR(50),
    team_lead_id INT,
    team_member_count INT
);
