tables.table_schema:
CREATE TABLE images (
    image_id UUID PRIMARY KEY,
    image_data BYTEA NOT NULL,
    uploaded_by_user_id UUID NOT NULL,
    upload_timestamp TIMESTAMP NOT NULL
);

CREATE TABLE annotations (
    annotation_id UUID PRIMARY KEY,
    image_id UUID REFERENCES images(image_id),
    annotation_text TEXT NOT NULL,
    created_by_user_id UUID NOT NULL,
    creation_timestamp TIMESTAMP NOT NULL
);

CREATE TABLE users (
    user_id UUID PRIMARY KEY,
    username TEXT UNIQUE NOT NULL,
    email TEXT NOT NULL
);

CREATE TABLE image_files (
    file_id UUID PRIMARY KEY,
    file_name VARCHAR(255),
    file_data BYTEA
);

CREATE TABLE annotations (
    annotation_id UUID PRIMARY KEY,
    file_id UUID REFERENCES image_files(file_id),
    user_id UUID,
    annotation_text TEXT,
    created_at TIMESTAMP
);

CREATE TABLE users (
    user_id UUID PRIMARY KEY,
    username VARCHAR(50),
    email VARCHAR(255)
);

CREATE TABLE whiteboard (
    whiteboard_id UUID PRIMARY KEY,
    file_id UUID REFERENCES image_files(file_id),
    user_id UUID REFERENCES users(user_id),
    created_at TIMESTAMP
);

CREATE TABLE Images (
    image_id INT PRIMARY KEY,
    image_name TEXT,
    image_data BYTEA
);

CREATE TABLE Users (
    user_id INT PRIMARY KEY,
    username TEXT,
    email TEXT
);

CREATE TABLE Whiteboards (
    whiteboard_id INT PRIMARY KEY,
    whiteboard_name TEXT
);

CREATE TABLE Whiteboard_Images (
    whiteboard_id INT,
    image_id INT,
    FOREIGN KEY (whiteboard_id) REFERENCES Whiteboards(whiteboard_id),
    FOREIGN KEY (image_id) REFERENCES Images(image_id)
);
