tables.table_schema:
CREATE TABLE meetings (
    meeting_id UUID PRIMARY KEY,
    meeting_date TIMESTAMP,
    agenda TEXT
);

CREATE TABLE participants (
    participant_id UUID PRIMARY KEY,
    meeting_id UUID,
    participant_name VARCHAR(255),
    participant_email VARCHAR(255)
);

CREATE TABLE agendas (
  agenda_id INT PRIMARY KEY,
  agenda_description TEXT,
  meeting_id INT,
  FOREIGN KEY (meeting_id) REFERENCES meetings(meeting_id)
);

CREATE TABLE attendees (
    attendee_id UUID PRIMARY KEY,
    name VARCHAR(255),
    email VARCHAR(255),
    role VARCHAR(255)
);

CREATE TABLE agenda_items (
    agenda_item_id UUID PRIMARY KEY,
    meeting_id UUID REFERENCES meetings(meeting_id),
    item_title VARCHAR(255),
    description TEXT
);

CREATE TABLE notes (
    note_id UUID PRIMARY KEY,
    meeting_id UUID REFERENCES meetings(meeting_id),
    attendee_id UUID REFERENCES attendees(attendee_id),
    note_text TEXT
);
