tables.table_schema:
CREATE TABLE MeetingRoles (
    role_id SERIAL PRIMARY KEY,
    role_name VARCHAR(50) NOT NULL,
    role_description TEXT
);

CREATE TABLE participant_roles (
    role_id INT PRIMARY KEY,
    role_name VARCHAR(50) NOT NULL,
    role_description TEXT
);

CREATE TABLE meeting_privileges (
    privilege_id INT PRIMARY KEY,
    privilege_name VARCHAR(50) NOT NULL,
    privilege_description TEXT
);

CREATE TABLE ParticipantRoles (
    participant_id UUID PRIMARY KEY,
    role_name TEXT,
    privileges JSONB
);

CREATE TABLE MeetingModerators (
    moderator_id UUID PRIMARY KEY,
    moderator_name TEXT,
    moderator_email TEXT UNIQUE,
    moderator_role TEXT
);

CREATE TABLE MeetingParticipants (
    participant_id UUID PRIMARY KEY,
    participant_name TEXT,
    participant_email TEXT UNIQUE,
    participant_roles JSONB
);

CREATE TABLE MeetingAttendees (
    attendee_id UUID PRIMARY KEY,
    attendee_name TEXT,
    attendee_email TEXT UNIQUE,
    attended_meeting_id UUID
);

CREATE TABLE Participants (
    participant_id INT PRIMARY KEY,
    participant_name VARCHAR(255),
    role VARCHAR(50),
    privilege VARCHAR(50)
);

CREATE TABLE Roles (
    role_id INT PRIMARY KEY,
    role_name VARCHAR(50),
    role_description TEXT
);

CREATE TABLE Privileges (
    privilege_id INT PRIMARY KEY,
    privilege_name VARCHAR(50),
    privilege_description TEXT
);
