tables.table_schema:
CREATE TABLE meeting_recordings (
    recording_id INT PRIMARY KEY,
    meeting_date DATE,
    recording_url TEXT,
    duration_minutes INT
);

CREATE TABLE meeting_transcripts (
    transcript_id INT PRIMARY KEY,
    recording_id INT REFERENCES meeting_recordings(recording_id),
    transcript_text TEXT,
    processed BOOLEAN
);

CREATE TABLE recordings (
    meeting_id INT PRIMARY KEY,
    recording_url VARCHAR(255),
    recording_date TIMESTAMP,
    duration_minutes INT
);

CREATE TABLE transcripts (
    transcript_id INT PRIMARY KEY,
    meeting_id INT,
    transcript_text TEXT,
    transcript_created TIMESTAMP
);

CREATE TABLE meetings (
    meeting_id INT PRIMARY KEY,
    meeting_title VARCHAR(100),
    meeting_date TIMESTAMP,
    organizer_name VARCHAR(50)
);

CREATE TABLE meetings (
  meeting_id INT PRIMARY KEY,
  meeting_topic VARCHAR(255),
  recording_url VARCHAR(255),
  transcript TEXT
);

CREATE TABLE keywords (
  keyword_id INT PRIMARY KEY,
  keyword_name VARCHAR(255),
  meeting_id INT,
  FOREIGN KEY (meeting_id) REFERENCES meetings(meeting_id)
);

CREATE TABLE MeetingRecordings (
  recording_id UUID PRIMARY KEY,
  meeting_id UUID,
  recording_url TEXT,
  recording_date DATE
);

CREATE TABLE MeetingTranscripts (
  transcript_id UUID PRIMARY KEY,
  meeting_id UUID,
  transcript_text TEXT,
  transcript_timestamp TIMESTAMP
);

CREATE TABLE MeetingAttendees (
  attendee_id UUID PRIMARY KEY,
  meeting_id UUID,
  attendee_name TEXT,
  attendee_email TEXT
);

CREATE TABLE MeetingTopics (
  topic_id UUID PRIMARY KEY,
  meeting_id UUID,
  topic_title TEXT,
  topic_description TEXT
);

CREATE TABLE MeetingFeedback (
  feedback_id UUID PRIMARY KEY,
  meeting_id UUID,
  feedback_text TEXT,
  feedback_rating INT
);
