tables.table_schema:
CREATE TABLE file_search_files (
    file_id INT PRIMARY KEY,
    file_name VARCHAR(255),
    file_path TEXT,
    file_keywords TEXT
);

CREATE TABLE file_search_keywords (
    keyword_id INT PRIMARY KEY,
    keyword_name VARCHAR(50)
);

CREATE TABLE files (
    file_id UUID PRIMARY KEY,
    file_name VARCHAR(255) NOT NULL,
    file_path VARCHAR(255) NOT NULL,
    keywords VARCHAR(255)[]
);

CREATE TABLE keywords (
    keyword_id UUID PRIMARY KEY,
    keyword VARCHAR(255) NOT NULL
);

CREATE TABLE file_keywords (
    file_id UUID REFERENCES files(file_id),
    keyword_id UUID REFERENCES keywords(keyword_id),
    PRIMARY KEY (file_id, keyword_id)
);

CREATE TABLE FileMetadata (
    file_id INT PRIMARY KEY,
    file_name VARCHAR(255),
    file_path VARCHAR(255),
    keywords VARCHAR(255)
);

CREATE TABLE FileTags (
    tag_id INT PRIMARY KEY,
    tag_name VARCHAR(50)
);

CREATE TABLE FileSearchHistory (
    search_id INT PRIMARY KEY,
    search_keyword VARCHAR(255),
    search_timestamp TIMESTAMP,
    search_result_count INT
);

CREATE TABLE files (
    file_id INT PRIMARY KEY,
    file_name VARCHAR(255),
    file_path VARCHAR(255),
    file_content TEXT
);

CREATE TABLE keywords (
    keyword_id INT PRIMARY KEY,
    keyword_text VARCHAR(100) UNIQUE
);

CREATE TABLE file_keywords (
    file_keyword_id INT PRIMARY KEY,
    file_id INT REFERENCES files(file_id),
    keyword_id INT REFERENCES keywords(keyword_id)
);

CREATE TABLE file_metadata (
    metadata_id INT PRIMARY KEY,
    file_id INT REFERENCES files(file_id),
    file_size INT,
    created_at TIMESTAMP
);

CREATE TABLE file_tags (
    tag_id INT PRIMARY KEY,
    file_id INT REFERENCES files(file_id),
    tag_name VARCHAR(50)
);
