tables.table_schema:
CREATE TABLE task_dependencies (
    task_id INT PRIMARY KEY,
    dependency_id INT
);

CREATE TABLE task_dependencies (
    task_id INT PRIMARY KEY,
    dependent_task_id INT
);

CREATE TABLE task_executions (
    execution_id INT PRIMARY KEY,
    task_id INT,
    execution_time TIMESTAMP
);

CREATE TABLE TaskDependencies (
  task_id INT PRIMARY KEY,
  dependent_task_id INT,
  FOREIGN KEY (dependent_task_id) REFERENCES TaskDependencies(task_id)
);

CREATE TABLE TaskDetails (
  task_id INT PRIMARY KEY,
  task_name VARCHAR(255),
  task_description TEXT
);

CREATE TABLE TaskSchedule (
  schedule_id INT PRIMARY KEY,
  task_id INT,
  scheduled_time TIMESTAMP,
  FOREIGN KEY (task_id) REFERENCES TaskDetails(task_id)
);

CREATE TABLE tasks (
    task_id SERIAL PRIMARY KEY,
    task_name VARCHAR(255) NOT NULL,
    dependency_id INT,
    CONSTRAINT fk_dependency FOREIGN KEY(dependency_id) REFERENCES tasks(task_id)
);

CREATE TABLE tasks (
 task_id INT PRIMARY KEY,
 task_name VARCHAR(255) NOT NULL,
 dependency_id INT,
 FOREIGN KEY(dependency_id) REFERENCES tasks(task_id)
);

CREATE TABLE dependencies (
 dependency_id INT PRIMARY KEY,
 dependency_name VARCHAR(255) NOT NULL
);

CREATE TABLE task_status (
 status_id INT PRIMARY KEY,
 task_id INT,
 status VARCHAR(50) NOT NULL,
 FOREIGN KEY(task_id) REFERENCES tasks(task_id)
);
