tables.table_schema:
CREATE TABLE third_party_tools (
    id INT PRIMARY KEY,
    tool_name TEXT NOT NULL,
    api_key TEXT
);

CREATE TABLE integration_platforms (
    id INT PRIMARY KEY,
    platform_name TEXT NOT NULL,
    api_endpoint TEXT
);

CREATE TABLE Integrations (
    integration_id UUID PRIMARY KEY,
    integration_name VARCHAR(255) NOT NULL,
    api_key VARCHAR(100) NOT NULL
);

CREATE TABLE ThirdPartyTools (
    tool_id UUID PRIMARY KEY,
    tool_name VARCHAR(255) NOT NULL,
    tool_description TEXT
);

CREATE TABLE PlatformFunctionality (
    functionality_id UUID PRIMARY KEY,
    functionality_name VARCHAR(255) NOT NULL,
    tool_id UUID REFERENCES ThirdPartyTools(tool_id)
);

CREATE TABLE third_party_tools (
    tool_id UUID PRIMARY KEY,
    tool_name STRING NOT NULL,
    tool_description STRING,
    tool_api_url STRING
);

CREATE TABLE services (
    service_id UUID PRIMARY KEY,
    service_name STRING NOT NULL,
    service_description STRING,
    service_api_key STRING
);

CREATE TABLE platform_integration (
    integration_id UUID PRIMARY KEY,
    tool_id UUID REFERENCES third_party_tools(tool_id),
    service_id UUID REFERENCES services(service_id),
    integration_status STRING
);

CREATE TABLE api_logs (
    log_id UUID PRIMARY KEY,
    integration_id UUID REFERENCES platform_integration(integration_id),
    log_timestamp TIMESTAMP,
    log_message STRING
);

CREATE TABLE third_party_apis (
    id INT PRIMARY KEY,
    api_name VARCHAR(255) NOT NULL,
    api_key VARCHAR(100) NOT NULL,
    integration_status BOOLEAN
);

CREATE TABLE platform_users (
    user_id INT PRIMARY KEY,
    username VARCHAR(50) NOT NULL,
    email VARCHAR(100) NOT NULL,
    role VARCHAR(20) NOT NULL
);

CREATE TABLE integration_logs (
    log_id INT PRIMARY KEY,
    api_name VARCHAR(255) NOT NULL,
    log_timestamp TIMESTAMP NOT NULL,
    log_description TEXT
);

CREATE TABLE platform_activities (
    activity_id INT PRIMARY KEY,
    user_id INT NOT NULL,
    activity_type VARCHAR(50) NOT NULL,
    activity_date DATE NOT NULL
);

CREATE TABLE service_providers (
    provider_id INT PRIMARY KEY,
    provider_name VARCHAR(100) NOT NULL,
    api_endpoint VARCHAR(255) NOT NULL,
    integration_date DATE NOT NULL
);
