tables.table_schema:
CREATE TABLE knowledge_taxonomy ( 
  topic_id SERIAL PRIMARY KEY, 
  topic_name VARCHAR(100) NOT NULL, 
  category VARCHAR(50) NOT NULL 
);

CREATE TABLE knowledge_topics (
    topic_id SERIAL PRIMARY KEY,
    topic_name VARCHAR(255) NOT NULL,
    category_id INT NOT NULL
);

CREATE TABLE knowledge_categories (
    category_id SERIAL PRIMARY KEY,
    category_name VARCHAR(255) NOT NULL
);

CREATE TABLE knowledge_topics (
    topic_id SERIAL PRIMARY KEY,
    topic_name TEXT NOT NULL,
    parent_topic_id INT,
    FOREIGN KEY (parent_topic_id) REFERENCES knowledge_topics(topic_id)
);

CREATE TABLE topic_categories (
    category_id SERIAL PRIMARY KEY,
    category_name TEXT NOT NULL,
    topic_id INT,
    FOREIGN KEY (topic_id) REFERENCES knowledge_topics(topic_id)
);

CREATE TABLE topics (
    topic_id INT PRIMARY KEY,
    topic_name VARCHAR(255) NOT NULL,
    parent_topic_id INT,
    FOREIGN KEY (parent_topic_id) REFERENCES topics(topic_id)
);

CREATE TABLE categories (
    category_id INT PRIMARY KEY,
    category_name VARCHAR(255) NOT NULL,
    topic_id INT,
    FOREIGN KEY (topic_id) REFERENCES topics(topic_id)
);

CREATE TABLE tags (
    tag_id INT PRIMARY KEY,
    tag_name VARCHAR(50) NOT NULL,
    article_id INT,
    FOREIGN KEY (article_id) REFERENCES articles(article_id)
);

CREATE TABLE topics (
  id INT PRIMARY KEY,
  name VARCHAR(255) NOT NULL,
  parent_id INT,
  FOREIGN KEY (parent_id) REFERENCES topics(id)
);

CREATE TABLE categories (
  id INT PRIMARY KEY,
  name VARCHAR(255) NOT NULL
);

CREATE TABLE users (
  id INT PRIMARY KEY,
  username VARCHAR(100) NOT NULL UNIQUE,
  email VARCHAR(255) NOT NULL UNIQUE
);
