tables.table_schema:
CREATE TABLE KnowledgeAssets (
    asset_id UUID PRIMARY KEY,
    asset_name STRING,
    asset_type STRING,
    creation_date TIMESTAMP
);

CREATE TABLE AssetLinks (
    link_id UUID PRIMARY KEY,
    source_asset_id UUID,
    target_asset_id UUID,
    link_type STRING,
    link_strength FLOAT
);

CREATE TABLE KnowledgeAssets (
    asset_id UUID PRIMARY KEY,
    asset_title VARCHAR(255),
    asset_type VARCHAR(50),
    asset_description TEXT
);
CREATE TABLE Relationships (
    relationship_id UUID PRIMARY KEY,
    from_asset_id UUID,
    to_asset_id UUID,
    relationship_type VARCHAR(50)
);
CREATE TABLE SemanticNetwork (
    network_id UUID PRIMARY KEY,
    network_name VARCHAR(100),
    creation_date TIMESTAMP,
    last_updated TIMESTAMP
);

CREATE TABLE knowledge_assets (
    asset_id INT,
    asset_name TEXT,
    asset_type TEXT,
    PRIMARY KEY (asset_id)
);

CREATE TABLE information_sources (
    source_id INT,
    source_name TEXT,
    source_type TEXT,
    PRIMARY KEY (source_id)
);

CREATE TABLE knowledge_network (
    network_id INT,
    link_id INT,
    PRIMARY KEY (network_id),
    FOREIGN KEY (link_id) REFERENCES asset_links(link_id)
);

CREATE TABLE KnowledgeAssets (
   id INT PRIMARY KEY,
   asset_name VARCHAR(255) NOT NULL,
   description TEXT,
   related_assets JSON
);

CREATE TABLE Tags (
   tag_id INT PRIMARY KEY,
   tag_name VARCHAR(50) NOT NULL
);
