tables.table_schema:
CREATE TABLE project_dependencies (
  project_id INT NOT NULL,
  dependent_project_id INT NOT NULL,
  PRIMARY KEY (project_id, dependent_project_id)
);

CREATE TABLE project_dependencies (
  project_id INT PRIMARY KEY,
  dependent_project_id INT,
  FOREIGN KEY (dependent_project_id) REFERENCES projects(project_id)
);

CREATE TABLE task_sequences (
  sequence_id INT PRIMARY KEY,
  task_name VARCHAR(255),
  project_id INT,
  FOREIGN KEY (project_id) REFERENCES projects(project_id)
);

CREATE TABLE critical_paths (
  path_id INT PRIMARY KEY,
  path_name VARCHAR(255),
  sequence_id INT,
  FOREIGN KEY (sequence_id) REFERENCES task_sequences(sequence_id)
);

CREATE TABLE Project (
    project_id INT PRIMARY KEY,
    project_name VARCHAR(255) NOT NULL,
    start_date DATE,
    end_date DATE
);

CREATE TABLE project_dependencies (
  project_id INT,
  dependency_id INT
);

CREATE TABLE task_sequences (
  task_id INT,
  sequence_id INT
);

CREATE TABLE critical_paths (
  path_id INT,
  task_id INT,
  duration INT
);

CREATE TABLE project_workflows (
  workflow_id INT,
  project_id INT,
  task_id INT
);

CREATE TABLE project_timelines (
  timeline_id INT,
  project_id INT,
  date DATE
);
