CREATE TABLE UserActivityLogs (
  log_id SERIAL PRIMARY KEY,
  user_id UUID,
  action VARCHAR(50),
  timestamp TIMESTAMP
);

CREATE TABLE FileSharingServiceUsers (
  user_id UUID PRIMARY KEY,
  username VARCHAR(50),
  email VARCHAR(100),
  registration_date DATE
);

CREATE TABLE user_activity_logs (
    log_id INT PRIMARY KEY,
    user_id INT,
    action VARCHAR,
    timestamp TIMESTAMP
);

CREATE TABLE users (
    user_id INT PRIMARY KEY,
    username VARCHAR,
    email VARCHAR,
    registration_date DATE
);

CREATE TABLE users ( 
  user_id INT PRIMARY KEY, 
  username VARCHAR(50), 
  email VARCHAR(100), 
  created_at TIMESTAMP 
);

CREATE TABLE files ( 
  file_id INT PRIMARY KEY, 
  file_name VARCHAR(100), 
  file_size INT, 
  created_at TIMESTAMP 
);

CREATE TABLE user_roles ( 
  user_id INT, 
  role_id INT, 
  PRIMARY KEY (user_id, role_id) 
);
