tables.table_schema:
CREATE TABLE users (
    id INT PRIMARY KEY,
    username VARCHAR(50),
    email VARCHAR(100),
    role VARCHAR(20)
);

CREATE TABLE Users (
    user_id UUID PRIMARY KEY,
    username VARCHAR(50) NOT NULL,
    email VARCHAR(100) NOT NULL,
    role VARCHAR(20) NOT NULL
);

CREATE TABLE Documents (
    doc_id UUID PRIMARY KEY,
    document_name VARCHAR(100) NOT NULL,
    owner_id UUID REFERENCES Users(user_id),
    created_at TIMESTAMP
);

CREATE TABLE Notifications (
    notification_id UUID PRIMARY KEY,
    recipient_id UUID REFERENCES Users(user_id),
    message TEXT NOT NULL,
    created_at TIMESTAMP
);

CREATE TABLE UserPermissions (
    permission_id UUID PRIMARY KEY,
    user_id UUID REFERENCES Users(user_id),
    document_id UUID REFERENCES Documents(doc_id),
    can_edit BOOLEAN NOT NULL
);

CREATE TABLE notifications (
  id INT PRIMARY KEY,
  user_id INT,
  message VARCHAR(255),
  timestamp TIMESTAMP
);

CREATE TABLE users (
  user_id INT PRIMARY KEY,
  username VARCHAR(50),
  email VARCHAR(100),
  role VARCHAR(20)
);

CREATE TABLE documents (
  doc_id INT PRIMARY KEY,
  doc_name VARCHAR(100),
  owner_id INT,
  created_at TIMESTAMP
);

CREATE TABLE document_users (
  id INT PRIMARY KEY,
  doc_id INT,
  user_id INT,
  access_level VARCHAR(10)
);

CREATE TABLE notifications_settings (
  user_id INT PRIMARY KEY,
  enable_push_notification BOOLEAN,
  enable_email_notification BOOLEAN
);
