CREATE TABLE feedback ( id serial PRIMARY KEY,
      user_id INT,
      message TEXT,
      created_at TIMESTAMP
);

CREATE TABLE users ( user_id serial PRIMARY KEY,
      email VARCHAR(255) NOT NULL,
      username VARCHAR(50) UNIQUE
);

CREATE TABLE notifications ( notification_id serial PRIMARY KEY,
      user_id INT,
      message TEXT,
      created_at TIMESTAMP,
      seen BOOLEAN
);

CREATE TABLE feedback_notifications (
      feedback_id INT,
      notification_id INT,
      FOREIGN KEY (feedback_id) REFERENCES feedback(id),
      FOREIGN KEY (notification_id) REFERENCES notifications(notification_id)
);

CREATE TABLE triggers ( trigger_id serial PRIMARY KEY,
      trigger_name VARCHAR(50) UNIQUE,
      table_name VARCHAR(50),
      trigger_action VARCHAR(50)
);
