tables.table_schema:
CREATE TABLE third_party_apps (
  app_id INT PRIMARY KEY,
  app_name VARCHAR(255) NOT NULL,
  app_description TEXT,
  app_integration_key VARCHAR(50) UNIQUE
);

CREATE TABLE third_party_applications (
    id INT PRIMARY KEY,
    app_name STRING,
    app_type STRING,
    integration_key STRING
);

CREATE TABLE data_exchanges (
    exchange_id INT PRIMARY KEY,
    exchange_type STRING,
    exchange_date DATE,
    app_id INT REFERENCES third_party_applications(id)
);

CREATE TABLE app_functionalities (
    function_id INT PRIMARY KEY,
    function_name STRING,
    function_description STRING,
    app_id INT REFERENCES third_party_applications(id)
);

CREATE TABLE third_party_applications (
    id INT PRIMARY KEY,
    name VARCHAR(50) NOT NULL,
    integration_type VARCHAR(50) NOT NULL,
    description TEXT
);

CREATE TABLE user_credentials (
    user_id INT PRIMARY KEY,
    username VARCHAR(50) NOT NULL,
    password VARCHAR(50) NOT NULL,
    email VARCHAR(50) UNIQUE
);
