tables.table_schema:
CREATE TABLE campaign_budget_allocation (
  campaign_id STRING PRIMARY KEY,
  budget_amount DECIMAL NOT NULL,
  allocated_date DATE
);

CREATE TABLE campaign_roi_tracking (
  campaign_id STRING PRIMARY KEY,
  total_sales DECIMAL NOT NULL,
  ad_impressions BIGINT
);

CREATE TABLE campaign_budgets (
    id INT PRIMARY KEY,
    campaign_name VARCHAR(255),
    budget_amount DECIMAL
);

CREATE TABLE campaign_roi (
    id INT PRIMARY KEY,
    campaign_name VARCHAR(255),
    spent_amount DECIMAL,
    revenue_generated DECIMAL
);

CREATE TABLE campaign_performance (
    id INT PRIMARY KEY,
    campaign_name VARCHAR(255),
    clicks INT,
    conversions INT
);

CREATE TABLE channels (
    channel_id INT PRIMARY KEY,
    channel_name VARCHAR(255) NOT NULL,
    channel_type VARCHAR(50) NOT NULL
);

CREATE TABLE allocations (
    allocation_id INT PRIMARY KEY,
    campaign_id INT,
    channel_id INT,
    allocation DECIMAL(10,2) NOT NULL
);
