tables.table_schema:
CREATE TABLE customers (
  customer_id INT PRIMARY KEY,
  name VARCHAR(50),
  email VARCHAR(100),
  location VARCHAR(100)
);

CREATE TABLE marketing_messages (
  message_id INT PRIMARY KEY,
  message_text TEXT,
  target_location VARCHAR(100)
);

CREATE TABLE geo_targets (
    id INT PRIMARY KEY,
    location_name VARCHAR(255),
    latitude DECIMAL(10, 8),
    longitude DECIMAL(11, 8)
);

CREATE TABLE marketing_messages (
    message_id INT PRIMARY KEY,
    message_text TEXT,
    start_date DATE,
    end_date DATE
);

CREATE TABLE location_based_marketing (
    message_id INT,
    id INT,
    FOREIGN KEY (message_id) REFERENCES marketing_messages(message_id),
    FOREIGN KEY (id) REFERENCES geo_targets(id)
);

CREATE TABLE location (
    id INT PRIMARY KEY,
    city VARCHAR(255),
    state VARCHAR(255),
    country VARCHAR(255)
);

CREATE TABLE message_stats (
    message_id INT,
    delivery_date DATE,
    opens INT,
    clicks INT
);

CREATE TABLE customer_preferences (
    preference_id INT PRIMARY KEY,
    customer_id INT,
    preference_name VARCHAR(255),
    preference_value VARCHAR(255)
);
