tables.table_schema:
CREATE TABLE support_ticket_trends (
 id INT PRIMARY KEY,
 ticket_number INT,
 status VARCHAR,
 opened_at TIMESTAMP
);

CREATE TABLE agent_performance (
 id INT PRIMARY KEY,
 agent_name VARCHAR,
 department VARCHAR,
 performance_score INT
);

CREATE TABLE customer_feedback (
 id INT PRIMARY KEY,
 customer_name VARCHAR,
 feedback_text TEXT,
 rating INT
);
