tables.table_schema:
CREATE TABLE lead_engagement (
    lead_id INT PRIMARY KEY,
    lead_name VARCHAR(50),
    lead_email VARCHAR(100),
    engagement_score INT
);

CREATE TABLE lead_behavior (
    behavior_id INT PRIMARY KEY,
    lead_id INT,
    behavior_type VARCHAR(50),
    behavior_timestamp TIMESTAMP
);

CREATE TABLE lead_scoring (
    lead_id INT PRIMARY KEY,
    overall_score INT,
    behavior_score INT,
    engagement_score INT
);

CREATE TABLE LeadBehavior (
    lead_id UUID PRIMARY KEY,
    behavior_type VARCHAR(50),
    behavior_timestamp TIMESTAMP,
    behavior_data JSONB
);

CREATE TABLE EngagementPatterns (
    pattern_id UUID PRIMARY KEY,
    pattern_type VARCHAR(50),
    pattern_description TEXT,
    pattern_score INT
);

CREATE TABLE LeadScoring (
    scoring_id UUID PRIMARY KEY,
    lead_id UUID REFERENCES LeadBehavior(lead_id),
    pattern_id UUID REFERENCES EngagementPatterns(pattern_id),
    scoring_result FLOAT
);

CREATE TABLE lead_behaviors (
    lead_id INT PRIMARY KEY,
    email VARCHAR(255),
    behavior_type VARCHAR(50),
    timestamp TIMESTAMP
);

CREATE TABLE engagement_patterns (
    pattern_id INT PRIMARY KEY,
    lead_id INT,
    pattern_name VARCHAR(50),
    pattern_score FLOAT
);

CREATE TABLE lead_scores (
    lead_id INT PRIMARY KEY,
    lead_score FLOAT,
    last_updated TIMESTAMP
);

CREATE TABLE behavior_scores (
    behavior_id INT PRIMARY KEY,
    behavior_score FLOAT,
    behavior_description TEXT
);

CREATE TABLE lead_engagement (
    engagement_id INT PRIMARY KEY,
    lead_id INT,
    engagement_type VARCHAR(50),
    engagement_date DATE
);
