tables.table_schema:
CREATE TABLE preferences (
    user_id INT PRIMARY KEY,
    theme VARCHAR(50),
    language VARCHAR(50),
    timezone VARCHAR(50)
);

CREATE TABLE leads (
    lead_id INT PRIMARY KEY,
    user_id INT,
    lead_name VARCHAR(50),
    lead_email VARCHAR(50)
);

CREATE TABLE communication_log (
  log_id INT,
  user_id INT,
  date_time TIMESTAMP,
  message TEXT
);

CREATE TABLE lead_status (
  lead_id INT,
  user_id INT,
  status VARCHAR(50),
  last_updated TIMESTAMP
);

CREATE TABLE Preferences (
  user_id UUID PRIMARY KEY,
  preference_1 TEXT,
  preference_2 TEXT
);

CREATE TABLE Users (
  user_id UUID PRIMARY KEY,
  user_name TEXT,
  email TEXT,
  phone_number TEXT
);

CREATE TABLE Subscriptions (
  subscription_id UUID PRIMARY KEY,
  user_id UUID,
  campaign_id UUID,
  subscribed_date DATE
);
