tables.table_schema:
CREATE TABLE social_media_posts (
    post_id UUID PRIMARY KEY,
    post_content STRING,
    user_id UUID,
    post_date TIMESTAMP
);

CREATE TABLE sentiment_analysis (
    sentiment_id UUID PRIMARY KEY,
    post_id UUID REFERENCES social_media_posts(post_id),
    sentiment_score FLOAT,
    sentiment_category STRING
);

CREATE TABLE trend_identification (
    trend_id UUID PRIMARY KEY,
    trend_name STRING,
    trend_start_date TIMESTAMP,
    trend_end_date TIMESTAMP
);

CREATE TABLE social_media_accounts (
    account_id      INT PRIMARY KEY,
    account_name    VARCHAR(50),
    followers_count INT,
    posts_count     INT
);

CREATE TABLE hashtags (
    hashtag_id     INT PRIMARY KEY,
    hashtag_text   VARCHAR(50)
);
