tables.table_schema:
CREATE TABLE projects (
    project_id INT PRIMARY KEY,
    project_name VARCHAR(255),
    project_status VARCHAR(50)
);

CREATE TABLE projects (project_id INT PRIMARY KEY, project_name VARCHAR(255) NOT NULL, project_description TEXT, project_status VARCHAR(50) DEFAULT 'Pending', created_at TIMESTAMP DEFAULT NOW());

CREATE TABLE workflows (workflow_id INT PRIMARY KEY, workflow_name VARCHAR(255) NOT NULL, workflow_description TEXT, project_id INT, created_at TIMESTAMP DEFAULT NOW(), FOREIGN KEY (project_id) REFERENCES projects(project_id));

CREATE TABLE approvals (approval_id INT PRIMARY KEY, workflow_id INT, approved_by VARCHAR(255) NOT NULL, approval_status VARCHAR(50) DEFAULT 'Pending', created_at TIMESTAMP DEFAULT NOW(), FOREIGN KEY (workflow_id) REFERENCES workflows(workflow_id));

CREATE TABLE projects (
    project_id INT PRIMARY KEY,
    project_name VARCHAR(255),
    start_date DATE,
    end_date DATE
);
