tables.table_schema:
CREATE TABLE SyncDatabaseConnector ( 
    connector_id INT PRIMARY KEY, 
    connector_name VARCHAR(255), 
    connector_type ENUM('SQL', 'NoSQL'), 
    last_sync_date TIMESTAMP 
);

CREATE TABLE project_managers (
 id SERIAL PRIMARY KEY,
 name TEXT,
 email TEXT,
 company TEXT
 );

CREATE TABLE system_connectors (
 id SERIAL PRIMARY KEY,
 name TEXT,
 type TEXT,
 owner TEXT
 );

CREATE TABLE data_synchronization (
 id SERIAL PRIMARY KEY,
 source_system TEXT,
 target_system TEXT,
 sync_type TEXT
 );

CREATE TABLE users (
    user_id UUID PRIMARY KEY,
    user_name VARCHAR(255) NOT NULL,
    email VARCHAR(255) UNIQUE,
    role VARCHAR(50)
);

CREATE TABLE task_assignments (
    assignment_id UUID PRIMARY KEY,
    task_id UUID REFERENCES tasks(task_id),
    user_id UUID REFERENCES users(user_id),
    assign_date DATE
);

CREATE TABLE projects (
    project_id SERIAL PRIMARY KEY,
    project_name VARCHAR(255) NOT NULL,
    project_description TEXT,
    project_status VARCHAR(50) DEFAULT 'Active'
);

CREATE TABLE users (
    user_id SERIAL PRIMARY KEY,
    user_name VARCHAR(255) NOT NULL,
    user_email VARCHAR(255) UNIQUE NOT NULL
);

CREATE TABLE system_integration (
    integration_id SERIAL PRIMARY KEY,
    integration_name VARCHAR(255) NOT NULL,
    integration_description TEXT,
    integration_type VARCHAR(50) NOT NULL
);

CREATE TABLE synchronization_logs (
    log_id SERIAL PRIMARY KEY,
    integration_id INT NOT NULL,
    log_timestamp TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
    log_message TEXT
);
