tables.table_schema:
CREATE TABLE likes (
    user_id UUID,
    post_id UUID,
    like_timestamp TIMESTAMP
);

CREATE TABLE shares (
    share_id UUID,
    user_id UUID,
    post_id UUID,
    share_timestamp TIMESTAMP
);

CREATE TABLE posts (
    post_id UUID,
    user_id UUID,
    post_content TEXT,
    post_timestamp TIMESTAMP
);

CREATE TABLE users (
    user_id UUID,
    username TEXT,
    email TEXT,
    registration_timestamp TIMESTAMP
);
