CREATE TABLE student_grades (
  student_id INT PRIMARY KEY,
  assignment_name VARCHAR(100),
  grade DECIMAL(5,2)
);

CREATE TABLE Students (
  student_id INT PRIMARY KEY,
  student_name VARCHAR(50) NOT NULL,
  grade INT
);

CREATE TABLE Assignments (
  assignment_id INT PRIMARY KEY,
  assignment_name VARCHAR(50) NOT NULL,
  student_id INT,
  assignment_score INT
);

CREATE TABLE students (
  student_id INT PRIMARY KEY,
  student_name VARCHAR(50) NOT NULL,
  student_grade INT
);

CREATE TABLE assignments (
  assignment_id INT PRIMARY KEY,
  assignment_name VARCHAR(50) NOT NULL,
  assignment_due_date DATE,
  assignment_grade INT
);

CREATE TABLE Students (student_id INT, student_name TEXT, student_email TEXT, PRIMARY KEY (student_id));

CREATE TABLE Assignments (assignment_id INT, assignment_name TEXT, total_marks INT, due_date DATE, PRIMARY KEY (assignment_id));

CREATE TABLE Grades (student_id INT, assignment_id INT, marks_obtained INT, FOREIGN KEY (student_id) REFERENCES Students(student_id), FOREIGN KEY (assignment_id) REFERENCES Assignments(assignment_id));

CREATE TABLE Performance (student_id INT, overall_grade TEXT, total_marks_obtained INT, FOREIGN KEY (student_id) REFERENCES Students(student_id));

CREATE TABLE students (
    student_id UUID PRIMARY KEY,
    student_name VARCHAR(50),
    student_email VARCHAR(100)
);

CREATE TABLE assignments (
    assignment_id UUID PRIMARY KEY,
    assignment_name VARCHAR(100),
    assignment_description TEXT,
    deadline DATE
);

CREATE TABLE course_registration (
    registration_id UUID PRIMARY KEY,
    course_id UUID,
    student_id UUID,
    registration_date DATE
);
