tables.table_schema:
CREATE TABLE StudentProgress (
    student_id SERIAL PRIMARY KEY,
    quiz_score INT,
    assignment_score INT,
    exam_score INT
);

CREATE TABLE QuizRecords (
    quiz_id SERIAL PRIMARY KEY,
    student_id INT REFERENCES StudentProgress(student_id),
    quiz_date DATE,
    quiz_score INT
);

CREATE TABLE QuizProgress (
  student_id UUID,
  quiz_id UUID,
  score FLOAT,
  completed_at TIMESTAMP
);

CREATE TABLE AssignmentProgress (
  student_id UUID,
  assignment_id UUID,
  grade FLOAT,
  submitted_at TIMESTAMP
);

CREATE TABLE ExamProgress (
  student_id UUID,
  exam_id UUID,
  result FLOAT,
  taken_at TIMESTAMP
);

