tables.table_schema:
CREATE TABLE virtual_classrooms (
    classroom_id INT PRIMARY KEY,
    classroom_name VARCHAR(50) NOT NULL,
    schedule_datetime TIMESTAMP NOT NULL,
    instructor_name VARCHAR(50) NOT NULL
);

CREATE TABLE classrooms (
    classroom_id INT PRIMARY KEY,
    classroom_name VARCHAR(255),
    instructor_name VARCHAR(255),
    schedule DATETIME
);

CREATE TABLE virtual_classrooms (
   id INT PRIMARY KEY,
   classroom_name VARCHAR(255),
   schedule_timestamp TIMESTAMP,
   moderator_id INT
);

CREATE TABLE online_discussions (
   id INT PRIMARY KEY,
   topic VARCHAR(255),
   discussion_date DATE,
   host_id INT
);

CREATE TABLE participants (
   id INT PRIMARY KEY,
   participant_name VARCHAR(255),
   participant_email VARCHAR(255),
   participant_type VARCHAR(50)
);

CREATE TABLE online_discussions (
    discussion_id UUID PRIMARY KEY,
    topic VARCHAR(255),
    discussion_date DATE,
    speaker_id UUID
);

CREATE TABLE instructors (
    instructor_id UUID PRIMARY KEY,
    instructor_name VARCHAR(255),
    email VARCHAR(255),
    department VARCHAR(255)
);

CREATE TABLE students (
    student_id UUID PRIMARY KEY,
    student_name VARCHAR(255),
    email VARCHAR(255),
    enrolled_courses VARCHAR(255)
);
