tables.table_schema:
CREATE TABLE personalized_study_recommendations (
    student_id INT,
    course_id INT,
    learning_history TEXT,
    performance_score FLOAT
);

CREATE TABLE student (student_id INT, first_name VARCHAR(50), last_name VARCHAR(50), email VARCHAR(100));
CREATE TABLE course (course_id INT, course_name VARCHAR(100), instructor VARCHAR(50), credits INT);
CREATE TABLE enrollment (enrollment_id INT, student_id INT, course_id INT, enrollment_date DATE);
CREATE TABLE performance (student_id INT, course_id INT, grade VARCHAR(2));
