tables.table_schema:
CREATE TABLE campaign_performance_metrics (
    campaign_id INT PRIMARY KEY,
    impressions INT,
    clicks INT,
    conversions INT
);

CREATE TABLE customer_engagement_metrics (
    customer_id INT PRIMARY KEY,
    email_opens INT,
    email_clicks INT,
    website_visits INT
);

CREATE TABLE customer_engagement (
  id INT PRIMARY KEY,
  customer_id INT,
  engagement_type VARCHAR(50),
  timestamp TIMESTAMP
);

CREATE TABLE customer_metrics (
  id INT PRIMARY KEY,
  customer_id INT,
  lifetime_value DECIMAL(10, 2),
  purchase_count INT
);

CREATE TABLE campaigns (
  campaign_id INT PRIMARY KEY,
  campaign_name VARCHAR(255),
  start_date DATE,
  end_date DATE
);

CREATE TABLE customers (
  customer_id INT PRIMARY KEY,
  customer_name VARCHAR(255),
  email VARCHAR(255),
  phone_number VARCHAR(20)
);
