tables.table_schema:
CREATE TABLE remote_work_productivity_data (
    user_id INT PRIMARY KEY,
    username VARCHAR(50),
    activity_time TIMESTAMP,
    productivity_score FLOAT
);

CREATE TABLE workflow_optimization (
    analysis_id SERIAL PRIMARY KEY,
    process_name VARCHAR(50),
    optimization_strategy TEXT,
    result VARCHAR(100)
);

CREATE TABLE productivity_data (
    user_id INT,
    date DATE,
    productivity_score FLOAT
);

CREATE TABLE projects (
    project_id INT,
    project_name TEXT,
    manager_id INT
);

CREATE TABLE users (
    user_id INT,
    username TEXT,
    position TEXT
);

CREATE TABLE remote_work_teams (
 team_id INT PRIMARY KEY,
 team_name VARCHAR(50) NOT NULL,
 team_leader VARCHAR(50) NOT NULL
);

CREATE TABLE employee (
 employee_id INT PRIMARY KEY,
 employee_name VARCHAR(50) NOT NULL,
 team_id INT REFERENCES remote_work_teams(team_id) NOT NULL,
 productivity_score FLOAT NOT NULL
);

CREATE TABLE tasks (
 task_id INT PRIMARY KEY,
 task_name VARCHAR(50) NOT NULL,
 task_description TEXT,
 deadline DATE
);

CREATE TABLE task_assignments (
 assignment_id INT PRIMARY KEY,
 task_id INT REFERENCES tasks(task_id) NOT NULL,
 employee_id INT REFERENCES employee(employee_id) NOT NULL,
 completion_status BOOLEAN NOT NULL
);

CREATE TABLE productivity_logs (
 log_id INT PRIMARY KEY,
 employee_id INT REFERENCES employee(employee_id) NOT NULL,
 task_id INT REFERENCES tasks(task_id) NOT NULL,
 log_timestamp TIMESTAMP NOT NULL
);
