tables.table_schema:
CREATE TABLE attendance_data (
    student_id INT,
    lecture_date DATE,
    attendance_status VARCHAR(10)
);

CREATE TABLE virtual_lecture_attendance (
    student_id INT,
    lecture_id INT,
    attendance_status ENUM('Present', 'Absent'),
    attendance_time TIMESTAMP
);

CREATE TABLE workshop_attendance (
    student_id INT,
    workshop_id INT,
    attendance_status ENUM('Attended', 'Missed'),
    workshop_date DATE
);

CREATE TABLE lecture_attendance (
  id INT PRIMARY KEY,
  student_id INT,
  lecture_date DATE,
  attendance_status VARCHAR(10)
);

CREATE TABLE workshop_attendance (
  id INT PRIMARY KEY,
  student_id INT,
  workshop_date DATE,
  attendance_status VARCHAR(10)
);

CREATE TABLE student_info (
  student_id INT PRIMARY KEY,
  student_name VARCHAR(50),
  student_email VARCHAR(50),
  enrollment_status VARCHAR(20)
);

CREATE TABLE student_attendance (
    student_id INT PRIMARY KEY,
    lecture_id INT,
    workshop_id INT,
    attendance_status BOOLEAN
);

CREATE TABLE lecture (
    lecture_id INT PRIMARY KEY,
    lecture_name VARCHAR(50),
    lecture_date DATE,
    lecture_time TIME
);

CREATE TABLE workshop (
    workshop_id INT PRIMARY KEY,
    workshop_name VARCHAR(50),
    workshop_date DATE,
    workshop_time TIME
);

CREATE TABLE student_attendance (
  student_id INT PRIMARY KEY,
  lecture_attendance BOOL,
  workshop_attendance BOOL
);

CREATE TABLE lecture_schedule (
  lecture_id INT PRIMARY KEY,
  lecture_topic TEXT,
  lecture_date DATE
);

CREATE TABLE workshop_schedule (
  workshop_id INT PRIMARY KEY,
  workshop_topic TEXT,
  workshop_date DATE
);

CREATE TABLE attendance_log (
  log_id INT PRIMARY KEY,
  student_id INT,
  event_type TEXT,
  event_date TIMESTAMP
);

CREATE TABLE engagement_metrics (
  engagement_id INT PRIMARY KEY,
  student_id INT,
  lecture_engagement_score INT,
  workshop_engagement_score INT
);
