tables.table_schema:
CREATE TABLE CoursePrerequisites (
    course_id INT PRIMARY KEY,
    course_name VARCHAR(255) NOT NULL,
    prerequisites TEXT
);

CREATE TABLE course_prerequisites (
course_id INT PRIMARY KEY,
prerequisite_id INT
);

CREATE TABLE course_sequence (
sequence_id SERIAL PRIMARY KEY,
course_id INT,
next_course_id INT
);

CREATE TABLE Course (course_id INT PRIMARY KEY, course_name VARCHAR(255) NOT NULL, credits INT NOT NULL);
CREATE TABLE Prerequisite (prereq_id INT PRIMARY KEY, course_id INT NOT NULL, prereq_course_id INT NOT NULL, FOREIGN KEY (course_id) REFERENCES Course(course_id), FOREIGN KEY (prereq_course_id) REFERENCES Course(course_id));
CREATE TABLE CourseSequence (sequence_id INT PRIMARY KEY, course_id INT NOT NULL, sequence_num INT NOT NULL, FOREIGN KEY (course_id) REFERENCES Course(course_id));
CREATE TABLE StudentProgression (student_id INT PRIMARY KEY, course_id INT NOT NULL, sequence_id INT NOT NULL, semester_completed INT NOT NULL, grade VARCHAR(2) NOT NULL, FOREIGN KEY (course_id) REFERENCES Course(course_id), FOREIGN KEY (sequence_id) REFERENCES CourseSequence(sequence_id));

CREATE TABLE Course (
    course_id INT PRIMARY KEY,
    course_name VARCHAR(100),
    credits INT
);

CREATE TABLE Sequence (
    sequence_id INT PRIMARY KEY,
    course_id INT,
    sequence_number INT,
    FOREIGN KEY (course_id) REFERENCES Course(course_id)
);

CREATE TABLE Student (
    student_id INT PRIMARY KEY,
    student_name VARCHAR(100),
    major VARCHAR(50)
);
