tables.table_schema:
CREATE TABLE asset_ownership (
    asset_id SERIAL PRIMARY KEY,
    owner_address VARCHAR(255),
    blockchain_platform VARCHAR(50),
    creation_timestamp TIMESTAMP
);

CREATE TABLE asset_trading_history (
    trade_id SERIAL PRIMARY KEY,
    asset_id INT REFERENCES asset_ownership(asset_id),
    trader_address VARCHAR(255),
    trade_timestamp TIMESTAMP
);

CREATE TABLE digital_assets (
	digital_asset_id INT PRIMARY KEY,
	digital_asset_name VARCHAR(255),
	owner_address VARCHAR(255),
	blockchain_platform VARCHAR(50)
);

CREATE TABLE blockchain_platforms (
	platform_id INT PRIMARY KEY,
	platform_name VARCHAR(50),
	platform_description TEXT
);

CREATE TABLE digital_assets (
    asset_id INT PRIMARY KEY,
    asset_name VARCHAR(100),
    asset_platform VARCHAR(50),
    asset_owner VARCHAR(50) NOT NULL
);

CREATE TABLE blockchain_platforms (
    platform_id INT PRIMARY KEY,
    platform_name VARCHAR(50) UNIQUE
);

CREATE TABLE blockchain_transactions (
    transaction_id INT PRIMARY KEY,
    transaction_hash VARCHAR(255) UNIQUE,
    transaction_date TIMESTAMP,
    transaction_platform_id INT REFERENCES blockchain_platforms(platform_id)
);
