tables.table_schema:
CREATE TABLE user_verification (
    user_id INT PRIMARY KEY,
    full_name VARCHAR(50),
    address VARCHAR(100),
    id_proof TEXT
);

CREATE TABLE loan_history (
    loan_id INT PRIMARY KEY,
    user_id INT REFERENCES user_verification(user_id),
    loan_amount DECIMAL(12, 2),
    repayment_status VARCHAR(20)
);

CREATE TABLE users (
    user_id INT PRIMARY KEY,
    username VARCHAR(50) NOT NULL,
    identity_document VARCHAR(100) NOT NULL
);

CREATE TABLE loans (
    loan_id INT PRIMARY KEY,
    amount DECIMAL(10, 2) NOT NULL,
    user_id INT NOT NULL,
    FOREIGN KEY (user_id) REFERENCES users(user_id)
);

CREATE TABLE users (
    user_id SERIAL PRIMARY KEY,
    username VARCHAR(50) NOT NULL,
    email VARCHAR(100) UNIQUE,
    password VARCHAR(255) NOT NULL
);

CREATE TABLE user_identity (
  id INT PRIMARY KEY,
  name VARCHAR(255),
  email VARCHAR(255) UNIQUE,
  phone_number VARCHAR(15)
);

CREATE TABLE loan_application (
  application_id INT PRIMARY KEY,
  user_id INT REFERENCES user_identity(id),
  amount DECIMAL,
  date_applied DATE
);

CREATE TABLE document_verification (
  verification_id INT PRIMARY KEY,
  user_id INT REFERENCES user_identity(id),
  document_type VARCHAR(50),
  is_verified BOOLEAN
);

CREATE TABLE collateral (
  collateral_id INT PRIMARY KEY,
  user_id INT REFERENCES user_identity(id),
  asset_type VARCHAR(50),
  value DECIMAL
);
