tables.table_schema:
CREATE TABLE UserPreferences (
    user_id INT PRIMARY KEY,
    risk_tolerance VARCHAR(50),
    investment_horizon INT,
    preferred_assets TEXT
);

CREATE TABLE user_preferences (
  user_id SERIAL PRIMARY KEY,
  username VARCHAR(50) NOT NULL,
  email VARCHAR(50) NOT NULL,
  investment_limit DECIMAL(10, 2) DEFAULT 0.00
);

CREATE TABLE automated_strategies (
  strategy_id SERIAL PRIMARY KEY,
  strategy_name VARCHAR(50) NOT NULL,
  description TEXT,
  strategy_type VARCHAR(20) NOT NULL
);

CREATE TABLE platform_settings (
  setting_id SERIAL PRIMARY KEY,
  platform_name VARCHAR(50) NOT NULL,
  platform_url VARCHAR(100) NOT NULL,
  api_key VARCHAR(50) NOT NULL
);

CREATE TABLE user_strategies (
  user_id SERIAL,
  strategy_id SERIAL,
  FOREIGN KEY (user_id) REFERENCES user_preferences(user_id),
  FOREIGN KEY (strategy_id) REFERENCES automated_strategies(strategy_id)
);

CREATE TABLE user_preferences (
    user_id INT PRIMARY KEY,
    preferred_currency VARCHAR(50),
    notification_settings VARCHAR(100),
    risk_tolerance INT
);

CREATE TABLE users (
    user_id INT PRIMARY KEY,
    username VARCHAR(50),
    email VARCHAR(100),
    creation_date DATE
);

CREATE TABLE platform_preferences (
    platform_id INT PRIMARY KEY,
    platform_name VARCHAR(50),
    preferred_strategy INT,
    FOREIGN KEY (preferred_strategy) REFERENCES investment_strategies(strategy_id)
);
