tables.table_schema:
CREATE TABLE cryptocurrency_exchanges (
  exchange_id INT PRIMARY KEY,
  exchange_name VARCHAR(50),
  country VARCHAR(50),
  registration_date DATE
);

CREATE TABLE exchanges (
    exchange_id SERIAL PRIMARY KEY,
    exchange_name TEXT,
    location TEXT,
    country TEXT
);

CREATE TABLE currencies (
    currency_id SERIAL PRIMARY KEY,
    currency_name TEXT,
    symbol TEXT,
    decimal_places INT
);

CREATE TABLE users (
    user_id SERIAL PRIMARY KEY,
    user_name TEXT,
    email TEXT,
    registration_date DATE
);

CREATE TABLE fees (
    fee_id SERIAL PRIMARY KEY,
    fee_type TEXT,
    amount DECIMAL,
    currency_id INT
);
