tables.table_schema:
CREATE TABLE users (
    id UUID PRIMARY KEY,
    name VARCHAR,
    email VARCHAR UNIQUE,
    tax_id VARCHAR UNIQUE
);

CREATE TABLE users (
    user_id UUID PRIMARY KEY,
    name TEXT,
    email TEXT,
    registration_date DATE
);

CREATE TABLE investors (
  investor_id INT PRIMARY KEY,
  investor_name VARCHAR(50),
  total_investment DECIMAL
);

CREATE TABLE holdings (
  investor_id INT,
  cryptocurrency VARCHAR(50),
  amount DECIMAL,
  PRIMARY KEY (investor_id, cryptocurrency)
);
