tables.table_schema:
CREATE TABLE blockchain_transactions (
  transaction_id SERIAL PRIMARY KEY,
  claim_id INT REFERENCES insurance_claims(claim_id),
  block_number BIGINT,
  timestamp TIMESTAMP
);

CREATE TABLE PolicyHolders (
    policy_holder_id UUID PRIMARY KEY,
    policy_holder_name VARCHAR(100) NOT NULL,
    policy_number VARCHAR(50) NOT NULL,
    blockchain_id UUID NOT NULL
);

CREATE TABLE BlockchainTransactions (
    transaction_id UUID PRIMARY KEY,
    claim_id UUID NOT NULL,
    payment_id UUID NOT NULL,
    transaction_date TIMESTAMP NOT NULL
);

CREATE TABLE insurance_claims (
 claim_id SERIAL PRIMARY KEY,
 policy_id VARCHAR(50) NOT NULL,
 claim_amount DECIMAL NOT NULL,
 claim_status VARCHAR(20) NOT NULL
);

CREATE TABLE payments (
 payment_id SERIAL PRIMARY KEY,
 claim_id INT NOT NULL,
 payment_amount DECIMAL NOT NULL,
 payment_date DATE NOT NULL
);

CREATE TABLE users (
 user_id SERIAL PRIMARY KEY,
 user_name VARCHAR(50) NOT NULL,
 user_email VARCHAR(100) NOT NULL
);

CREATE TABLE blockchain_transactions (
 transaction_id SERIAL PRIMARY KEY,
 claim_id INT NOT NULL,
 blockchain_hash VARCHAR(100) NOT NULL,
 transaction_date TIMESTAMP NOT NULL
);

CREATE TABLE payment_history (
 history_id SERIAL PRIMARY KEY,
 payment_id INT NOT NULL,
 user_id INT NOT NULL,
 payment_status VARCHAR(20) NOT NULL
);
